{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://mova.dev/lab/schemas/ds.lab_battery_passport_extended_v1.schema.json",
  "title": "ds.lab_battery_passport_extended_v1",
  "description": "Extended lab-level battery passport for DPP pilots with CF, traceability, and external source references.",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "battery_id": {
      "type": "string",
      "description": "Identifier of the battery in the manufacturer system."
    },
    "manufacturer_name": {
      "type": "string",
      "description": "Manufacturer name."
    },
    "manufacturing_date": {
      "type": "string",
      "format": "date",
      "description": "Manufacturing date (local plant date)."
    },
    "manufacturing_location": {
      "type": "string",
      "description": "Manufacturing location (plant / city / country)."
    },
    "carbon_footprint": {
      "type": "object",
      "description": "Cradle-to-gate carbon footprint information.",
      "additionalProperties": false,
      "properties": {
        "value_kg_co2e": {
          "type": "number",
          "minimum": 0,
          "description": "Carbon footprint value in kg CO2e."
        },
        "method": {
          "type": "string",
          "description": "CFF method identifier or name."
        },
        "verification_status": {
          "type": "string",
          "description": "Verification status of the CF value (e.g. third_party_verified)."
        }
      },
      "required": [
        "value_kg_co2e",
        "method",
        "verification_status"
      ]
    },
    "materials": {
      "type": "object",
      "description": "Material traceability information.",
      "additionalProperties": false,
      "properties": {
        "cobalt": {
          "type": "object",
          "additionalProperties": false,
          "properties": {
            "source": {
              "type": "string",
              "description": "Source of cobalt (mine / supplier)."
            },
            "recycled_pct": {
              "type": "number",
              "minimum": 0,
              "maximum": 100,
              "description": "Share of recycled cobalt in percent."
            },
            "traceability": {
              "$ref": "https://mova.dev/lab/schemas/ds.lab_traceability_summary_v1.schema.json",
              "description": "Traceability summary for cobalt."
            }
          },
          "required": [
            "source",
            "traceability"
          ]
        }
      },
      "required": [
        "cobalt"
      ]
    },
    "external_sources": {
      "type": "array",
      "description": "External data sources (ERP/MES/CSV/Excel) that feed this passport.",
      "items": {
        "$ref": "https://mova.dev/lab/schemas/ds.lab_external_source_ref_v1.schema.json"
      }
    },
    "regulation_tags": {
      "type": "array",
      "description": "Free-form regulatory tags, e.g. 'EU_BATTERY_REG_2023.Annex_XIII.3b'.",
      "items": {
        "type": "string"
      }
    }
  },
  "required": [
    "battery_id",
    "manufacturer_name",
    "manufacturing_date",
    "manufacturing_location",
    "carbon_footprint",
    "materials"
  ]
}
