{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://mova.dev/lab/schemas/ds.lab_battery_passport_minimal_v1.schema.json",
  "title": "ds.lab_battery_passport_minimal_v1",
  "description": "Minimal lab-level battery passport for DPP pilots (without full Annex XIII coverage).",
  "type": "object",
  "additionalProperties": false,
  "properties": {
    "passport_id": {
      "type": "string",
      "description": "Stable identifier of the passport (may equal battery_id)."
    },
    "battery_basic": {
      "type": "object",
      "description": "Basic information about the battery and manufacturer.",
      "additionalProperties": false,
      "properties": {
        "battery_id": {
          "type": "string",
          "description": "Battery identifier in the manufacturer system."
        },
        "manufacturer_name": {
          "type": "string",
          "description": "Manufacturer name."
        },
        "manufacturer_country": {
          "type": "string",
          "description": "Country code of the manufacturer (e.g. DE)."
        },
        "manufacturing_date": {
          "type": "string",
          "format": "date",
          "description": "Manufacturing date (local plant date)."
        },
        "manufacturing_location": {
          "type": "string",
          "description": "Manufacturing location (plant / city / country)."
        }
      },
      "required": [
        "battery_id",
        "manufacturer_name",
        "manufacturer_country",
        "manufacturing_date",
        "manufacturing_location"
      ]
    },
    "carbon_footprint": {
      "type": "object",
      "description": "Cradle-to-gate carbon footprint in CFF-compatible format.",
      "additionalProperties": false,
      "properties": {
        "value": {
          "type": "number",
          "minimum": 0,
          "description": "Carbon footprint value."
        },
        "unit": {
          "type": "string",
          "const": "kg_CO2e",
          "description": "Unit of measurement."
        },
        "scope": {
          "type": "string",
          "enum": [
            "cradle_to_gate"
          ],
          "description": "CF scope."
        },
        "method": {
          "type": "string",
          "description": "Methodology / CFF version."
        },
        "verification_status": {
          "type": "string",
          "enum": [
            "self_declared",
            "third_party_verified",
            "unknown"
          ],
          "description": "Verification status of the CF value."
        },
        "regulation_requirement": {
          "$ref": "https://mova.dev/lab/schemas/ds.lab_regulation_requirement_v1.schema.json",
          "description": "Reference to the related regulatory requirement (e.g. Annex XIII clause for CF)."
        }
      },
      "required": [
        "value",
        "unit",
        "scope",
        "verification_status"
      ]
    },
    "materials": {
      "type": "object",
      "description": "Minimal material and traceability block.",
      "additionalProperties": false,
      "properties": {
        "cobalt": {
          "type": "object",
          "additionalProperties": false,
          "properties": {
            "source": {
              "type": "string",
              "description": "Source of cobalt (mine / supplier)."
            },
            "recycled_pct": {
              "type": "number",
              "minimum": 0,
              "maximum": 100,
              "description": "Share of recycled cobalt in percent."
            },
            "traceability": {
              "$ref": "https://mova.dev/lab/schemas/ds.lab_traceability_summary_v1.schema.json",
              "description": "Traceability summary for cobalt (known/unknown)."
            }
          },
          "required": [
            "traceability"
          ]
        }
      }
    },
    "data_sources": {
      "type": "array",
      "description": "External data sources feeding this passport (ERP/MES/CSV etc.).",
      "items": {
        "$ref": "https://mova.dev/lab/schemas/ds.lab_external_source_ref_v1.schema.json"
      }
    }
  },
  "required": [
    "passport_id",
    "battery_basic",
    "carbon_footprint",
    "materials"
  ]
}
