{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "$id": "https://mova.dev/lab/schemas/ds.lab_regulation_requirement_v1.schema.json",
  "title": "ds.lab_regulation_requirement_v1",
  "description": "Lab-level record of a single regulatory requirement (e.g., Annex XIII EU Battery Regulation) used to map DPP fields to legal clauses.",
  "type": "object",
  "properties": {
    "regulation_id": {
      "type": "string",
      "description": "Regulation identifier, e.g. EU_BATTERY_REG_2023."
    },
    "clause_ref": {
      "type": "string",
      "description": "Clause or annex reference, e.g. Annex_XIII.3b."
    },
    "data_point_id": {
      "type": "string",
      "description": "Logical data point id, e.g. battery.cf.cradle_to_gate."
    },
    "required_level": {
      "type": "string",
      "description": "Requirement level.",
      "enum": [
        "mandatory",
        "recommended",
        "optional"
      ]
    },
    "verification_method": {
      "type": "string",
      "description": "Brief verification method (e.g., third_party_LCA, internal_calculation, certificate_XYZ)."
    },
    "notes": {
      "type": "string",
      "description": "Additional human-readable notes/context."
    }
  },
  "required": [
    "regulation_id",
    "clause_ref",
    "data_point_id"
  ],
  "additionalProperties": false
}
